sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    var refreshMinutes = 2;
    const timeoutId = helpers.timing.setTimeout(() => {
         api.setState('listRefreshTime', Date.now());
    }, refreshMinutes * 60 * 1000);

    if (api.state.listFirstRefresh == true) {
        api.setState('listFirstRefresh', false);
        api.setState('listRefreshTime', Date.now());
    }

}
